package basis;

import javax.swing.*;
import java.awt.*;

/**
 * Fenster-Klasse für die basis-Bibliothek
 */
public class Fenster extends JFrame {
    private JPanel contentPanel;

    public Fenster() {
        setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        setLayout(null);
        setVisible(true);
    }

    public void setzeGroesse(int breite, int hoehe) {
        setSize(breite, hoehe);
    }

    public void setzeTitel(String titel) {
        setTitle(titel);
    }

    public void gibFrei() {
        dispose();
        System.exit(0);
    }

    @Override
    public Component add(Component comp) {
        return super.add(comp);
    }
}

package basis;

import javax.swing.*;
import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;

/**
 * Knopf-Klasse für die basis-Bibliothek
 */
public class Knopf extends JButton implements ActionListener {
    private KnopfLauscher lauscher;

    public Knopf(String text) {
        super(text);
        addActionListener(this);
    }

    public Knopf(String text, int x, int y, int breite, int hoehe) {
        super(text);
        setBounds(x, y, breite, hoehe);
        addActionListener(this);
    }

    public void setzeKnopfLauscher(KnopfLauscher l) {
        this.lauscher = l;
    }

    public void setzePosition(int x, int y) {
        setLocation(x, y);
    }

    public void setzeGroesse(int breite, int hoehe) {
        setSize(breite, hoehe);
    }

    @Override
    public void actionPerformed(ActionEvent e) {
        if (lauscher != null) {
            lauscher.bearbeiteKnopfDruck(this);
        }
    }
}

package basis;

/**
 * Interface für Knopf-Ereignisse
 */
public interface KnopfLauscher {
    void bearbeiteKnopfDruck(Knopf k);
}
